const triggerEvent = (websocket, event, data) => {
    const dataSet = {
      event,
      data,
    };
  
    websocket.send(JSON.stringify(dataSet));
};

const handleEvent = (websocket, event, callback) => {
    websocket.on('message', (data) => {
        const message = JSON.parse(data);
        
        if(message.event === event){
            callback(message.data);
        }
    });
}

module.exports = {
    triggerEvent,
    handleEvent,
};