const publishEvent = (websocket, channel, namespace, dataSet) => {
    const isClientChannel = channel.startsWith('client-');

    if (isClientChannel) {
        throw new Error('Cannot publish to client channels');
    }
    const data = {
      event: 'publish',
      channel,
      namespace,
      data: dataSet,
    };

    websocket.send(JSON.stringify(data));
  };
  
  module.exports = {
    publishEvent,
  };
  