const messageHistory = {};


const storeMessage = (channel, message) => {
    if (!messageHistory[channel]) {
        messageHistory[channel] = [];
    }
    messageHistory[channel].push(message);
}

const getMessageHistory = (channel, callback) => {
    if (messageHistory[channel]) {
        callback(messageHistory[channel]);
    }
}


module.exports = {
  storeMessage,
  getMessageHistory,
};
